mapper.update:
update activity_group
name=#{name}, desCription=#{description}, icon=#{icon}, sequence=#{sequence}, price=#{price}
where id=#{id}

mapper.select:
select id, name, icon, sequence, description, price, create_date as createDate, update_date as updateDate from activity_group where enable=1
and name like concat('%',#{name},'%')
order by #{page.sort} #{page.order}
limit #{page.start},#{page.end}
