mapper.update:
update user
name=#{name}, account=#{account}, mobile=#{mobile}, update_date=#{updateDate}, salt=#{salt}, PassWord=#{password}, last_logon_date=#{lastLogonDate}
where id=#{id}

mapper.select:
SELECT count(0) from user AS u1 left join user_role AS u2 on u1.id=u2.user_id left join role AS r on u2.role_id=r.id where u1.enable=1
and r.id=#{roleid}
and u1.name like concat('%',#{name},'%')

SELECT u1.id, u1.name, u1.account, u1.password, u1.salt, u1.mobile, u1.qq, u1.email, u1.create_date as createDate, r.name AS rolename from user AS u1 left join user_role AS u2 on u1.id=u2.user_id left join role AS r on u2.role_id=r.id where u1.enable=1
and r.id=#{roleid}
and u1.name like concat('%',#{name},'%')
order by u1.${page.sort} ${page.order}
limit #{page.start},#{page.end}
